export default {
  data: function data() {
    return {
      pid: "",
      bImglist: [],
      dname: "",
      dtype: "",
      dprice: "",
      count: 1,
      dcount: "",
      dImglist: [],
      wsc_dp: "xs_in",
      ysc_dp: "yc",
      pdetails_dp: "xs",
      csdetails_dp: "yc",
      pjdetails_dp: "yc",
      bottomTips_dp: "",
      buy_qx: "",
      addCar_qx: "",
      addCar_dp: "",
      buy_dp: "",
      car_dp: "xs",
      fh_dp: "xs",
      xqbtn_dp: "active1",
      csbtn_dp: "",
      pjbtn_dp: "",
      xqhk_dp: "active2",
      cshk_dp: "",
      pjhk_dp: "",
      top_dp: "",
      n: 1,
      csImg: "",
      allp_dp: "pjactive",
      haop_dp: "",
      zhongp_dp: "",
      chap_dp: "",
      details_dp: "xs",
      selBg_dp: "yc",
      selected_dp: "ycWy",
      selpic: "",
      selprice: "",
      seldtype: "",
      dftype: "",
      seltype0: "",
      seltype1: "",
      seltype0_dp: "typeActive xs",
      seltype1_dp: "xs",
      xzTypes_dp: "",
      data: {},
      kcText: "",
      mrState: 1,
      xzState: "",
      xzPrice: "",
      xzPic: "",
      xzCount: "",
      xzDtype: "",
      countText: "1",
      dpid: "",
      zxDpid: "",
      carNum: "",
      gwcNum_dp: "yc",
      gwcNum1_dp: "yc"
    };
  },
  methods: {
    goCar: function goCar() {
      if (sessionStorage.getItem("uid") == null) {
        this.$router.push("/Login");
      } else {
        this.uid = sessionStorage.getItem("uid");
        this.$router.push({
          path: "/Car/".concat(this.uid)
        });
      }
    },
    selqrFun: function selqrFun() {
      this.selBg_dp = "yc";
      this.selected_dp = "ycWy";

      if (this.data.proText2[0] == null) {
        this.xzState = 1;
        this.zxDpid = this.data.proText1.dpid;
        this.xzPic = this.data.bannerImg1[0].lbtpic;
      } else {
        this.xzState = this.mrState;
      }

      this.xzDtype = this.seldtype;
      this.xzCount = this.count;
      this.dtype = this.xzDtype;
      this.countText = this.xzCount;
      this.details_dp = "xs";
      this.dprice = this.xzPrice;

      if (this.xzState == 0) {
        this.seltype1_dp = "typeActive";
        this.seltype0_dp = "";
        this.bImglist = this.data.bannerImg2;
        this.dImglist = this.data.detailImg2;
        this.csImg = this.data.csImages2[0].cspic;
        this.xzDpid = this.data.proText2[0].dpid;
        this.xzPic = this.data.bannerImg2[0].lbtpic;
      } else {
        this.xzState = 1;
        this.seltype0_dp = "typeActive";
        this.seltype1_dp = "";
        this.bImglist = this.data.bannerImg1;
        this.dImglist = this.data.detailImg1;
        this.csImg = this.data.csImages1[0].cspic;
        this.xzDpid = this.data.proText1[0].dpid;
        this.xzPic = this.data.bannerImg1[0].lbtpic;
      }
    },
    sub: function sub() {
      if (this.count > 1) {
        this.count--;
      } else {
        this.$toast({
          message: "不能再少啦~",
          position: "top"
        });
      }
    },
    add: function add() {
      if (this.pid == 1 && this.count < 6 && this.dcount == 1) {
        this.count++;
      } else if (this.pid !== 1 && this.count < 3 && this.dcount == 1) {
        this.count++;
      } else if (this.dcount == 1) {
        this.$toast({
          message: "不能再加啦~",
          position: "top"
        });
      } else {
        this.count = 1;
      }
    },
    sel0fun: function sel0fun() {
      if (this.data.proText2[0] == null) {
        this.seltype0_dp = "typeActive xs";
        this.seltype1_dp = "yc";
      } else {
        this.seltype0_dp = "typeActive xs";
        this.seltype1_dp = "xs";
      }

      this.mrState = this.data.proText1[0].mrState;
      this.seldtype = this.data.proText1[0].dtype;
      this.selprice = this.data.proText1[0].dprice;
      this.selpic = this.data.bannerImg1[0].lbtpic;
    },
    sel1fun: function sel1fun() {
      this.seltype0_dp = "";
      this.seltype1_dp = "typeActive xs";
      this.mrState = this.data.proText2[0].mrState;
      this.seldtype = this.data.proText2[0].dtype;
      this.selprice = this.data.proText2[0].dprice;
      this.selpic = this.data.bannerImg2[0].lbtpic;
    },
    closeSel: function closeSel() {
      this.selBg_dp = "yc";
      this.selected_dp = "ycWy";
      this.details_dp = "xs";
      this.count = this.xzCount;

      if (this.xzState == 1) {
        this.seltype0_dp = "typeActive xs";
        this.seltype1_dp = "xs";
      } else {
        this.seltype1_dp = "typeActive xs";
        this.seltype0_dp = "xs";
      }
    },
    selProduct: function selProduct() {
      if (this.dcount == 0) {
        this.$toast({
          message: "商品库存不足",
          position: "bottom"
        });
      }

      this.details_dp = "yc";
      this.selBg_dp = "xs";
      this.selected_dp = "xsWy";
      this.selpic = this.xzPic;
      this.selprice = this.xzPrice;

      if (this.xzDtype == null) {
        this.dftype = "";
        this.xzTypes_dp = "yc";
      } else {
        this.dftype = this.xzDtype;
        this.xzTypes_dp = "xs";
      }

      if (this.data.proText2[0] == null) {
        this.seltype0 = this.data.proText1[0].dtype;
        this.seltype1_dp = "yc";
        this.seldtype = this.seltype0;
        this.zxDpid = this.data.proText1.dpid;
        this.xzPic = this.data.bannerImg1[0].lbtpic;
      } else {
        this.seltype0 = this.data.proText1[0].dtype;
        this.seltype1 = this.data.proText2[0].dtype;
      }

      if (this.xzState == 1) {
        this.seldtype = this.xzDtype;
        this.seltype0_dp = "typeActive";
        this.zxDpid = this.data.proText1[0].dpid;
        this.xzPic = this.data.bannerImg1[0].lbtpic;
      } else {
        this.seldtype = this.xzDtype;
        this.seltype0_dp = "";
        this.seltype1_dp = "typeActive";
        this.zxDpid = this.data.proText2[0].dpid;
        this.xzPic = this.data.bannerImg2[0].lbtpic;
      }
    },
    allp: function allp() {
      this.allp_dp = "pjactive";
      this.haop_dp = "";
      this.zhongp_dp = "";
      this.chap_dp = "";
    },
    haop: function haop() {
      this.allp_dp = "";
      this.haop_dp = "pjactive";
      this.zhongp_dp = "";
      this.chap_dp = "";
    },
    chap: function chap() {
      this.allp_dp = "";
      this.haop_dp = "";
      this.zhongp_dp = "";
      this.chap_dp = "pjactive";
    },
    zhongp: function zhongp() {
      this.allp_dp = "";
      this.haop_dp = "";
      this.zhongp_dp = "pjactive";
      this.chap_dp = "";
    },
    goback: function goback() {
      this.$router.go(-1);
    },
    handleScroll: function handleScroll() {
      var scrollTop = window.pageYOffset || document.documentElement.scrollTop || document.body.scrollTop;

      if (scrollTop > 375 && this.n === 1) {
        this.top_dp = "tmd1";
      } else if (scrollTop > 300 && this.n === 1) {
        this.top_dp = "tmd5";
      } else if (scrollTop > 200 && this.n === 1) {
        this.top_dp = "tmd4";
      } else if (scrollTop > 100 && this.n === 1) {
        this.top_dp = "tmd3";
      } else if (scrollTop > 0 && this.n === 1) {
        this.top_dp = "tmd2";
      } else if (scrollTop == 0 && this.n === 1) {
        this.top_dp = "tmd0";
      } else {
        this.top_dp = "tmd1";
      }

      if (scrollTop == 0) {
        this.car_dp = "xs_in";
        this.fh_dp = "xs_in";
      } else {
        this.car_dp = "yc";
        this.fh_dp = "yc";
      }
    },
    addCar: function addCar() {
      var _this = this;

      if (sessionStorage.getItem("uid") == null) {
        this.$router.push("/Login");
      } else {
        this.uid = sessionStorage.getItem("uid");
        var objCar = {
          gpic: this.xzPic,
          gname: this.dname,
          gprice: this.xzPrice,
          gtype: this.xzDtype,
          gcount: this.xzCount,
          uid: this.uid,
          dpid: this.xzDpid,
          dchecked: this
        };
        var url = "addCar";
        this.axios.post(url, this.qs.stringify(objCar)).then(function (res) {
          console.log(res.data);

          if (res.data.code == 1) {
            _this.$toast({
              message: "已加入购物车",
              position: "bottom"
            });

            _this.getCount();
          }
        });
      }
    },
    getCount: function getCount() {
      var _this2 = this;

      if (sessionStorage.getItem("uid") !== null) {
        this.uid = sessionStorage.getItem("uid");
        var url = "gcount";
        var obj = {
          uid: this.uid
        };
        this.axios.get(url, {
          params: obj
        }).then(function (res) {
          if (res.data.code == 1) {
            _this2.carNum = res.data.data[0].gcount;

            if (_this2.carNum == 0) {
              _this2.gwcNum_dp = "yc";
              _this2.gwcNum1_dp = "yc";
            } else if (_this2.carNum == null) {
              _this2.gwcNum_dp = "yc";
              _this2.gwcNum1_dp = "yc";
            } else {
              _this2.gwcNum_dp = "xs_in";
              _this2.gwcNum1_dp = "xs_in";
            }
          } else {
            _this2.gwcNum_dp = "yc";
            _this2.gwcNum1_dp = "yc";
          }
        });
      } else {
        this.gwcNum_dp = "yc";
        this.gwcNum1_dp = "yc";
      }
    },
    xqfun: function xqfun() {
      this.xqbtn_dp = "active1";
      this.xqhk_dp = "active2";
      this.csbtn_dp = "";
      this.cshk_dp = "";
      this.pjbtn_dp = "";
      this.pjhk_dp = "";
      this.pdetails_dp = "xs";
      this.csdetails_dp = "yc";
      this.pjdetails_dp = "yc";
      this.n = 1;
      this.top_dp = "tmd0";
      var pid = this.pid;
    },
    csfun: function csfun() {
      this.xqbtn_dp = "";
      this.xqhk_dp = "";
      this.csbtn_dp = "active1";
      this.cshk_dp = "active2";
      this.pjbtn_dp = "";
      this.pjhk_dp = "";
      this.pdetails_dp = "yc";
      this.csdetails_dp = "xs";
      this.pjdetails_dp = "yc";
      this.n = 2;
      var pid = this.pid;
    },
    pjfun: function pjfun() {
      this.xqbtn_dp = "";
      this.xqhk_dp = "";
      this.csbtn_dp = "";
      this.cshk_dp = "";
      this.pjbtn_dp = "active1";
      this.pjhk_dp = "active2";
      this.pdetails_dp = "yc";
      this.csdetails_dp = "yc";
      this.pjdetails_dp = "xs";
      this.n = 3;
      var pid = this.pid;
    },
    onload: function onload() {
      var _this3 = this;

      this.pid = this.$route.params.pid;
      var obj = {
        pid: this.pid
      };
      this.axios.get("productDetail", {
        params: obj
      }).then(function (res) {
        _this3.data = res.data;

        _this3.getCount();

        _this3.bImglist = res.data.bannerImg1;
        _this3.dname = res.data.proText1[0].dname;
        _this3.dtype = res.data.proText1[0].dtype;
        _this3.dprice = res.data.proText1[0].dprice;
        _this3.csImg = res.data.csImages1[0].cspic;
        _this3.dcount = res.data.proText1[0].dcount;
        _this3.xzState = res.data.proText1[0].mrState;
        _this3.xzPrice = res.data.proText1[0].dprice;
        _this3.xzDtype = res.data.proText1[0].dtype;
        _this3.xzDpid = res.data.proText1[0].dpid;
        _this3.xzCount = 1;
        _this3.xzPic = res.data.bannerImg1[0].lbtpic;
        _this3.dpid = res.data.proText1[0].dpid;

        if (_this3.dcount === 1) {
          _this3.kcText = "有货", _this3.bottomTips_dp = "ych";
          _this3.addCar_qx = false;
          _this3.buy_qx = false;
          _this3.addCar_dp = "addCarB";
          _this3.buy_dp = "buyB";
        } else {
          _this3.kcText = "无货";
          _this3.bottomTips_dp = "xsh";
          _this3.addCar_qx = true;
          _this3.buy_qx = true;
          _this3.addCar_dp = "buttonJY";
          _this3.buy_dp = "buttonJY";
        }

        ;
        _this3.dImglist = res.data.detailImg1;
      });
    },
    backTop: function backTop() {
      var that = this;
      var timer = setInterval(function () {
        var ispeed = Math.floor(-that.scrollTop / 5);
        document.documentElement.scrollTop = document.body.scrollTop = that.scrollTop + ispeed;

        if (that.scrollTop === 0) {
          clearInterval(timer);
        }
      }, 16);
    },
    scrollToTop: function scrollToTop() {
      var that = this;
      var scrollTop = window.pageYOffset || document.documentElement.scrollTop || document.body.scrollTop;
      that.scrollTop = scrollTop;
    }
  },
  created: function created() {
    this.onload();
    this.backTop();
  },
  mounted: function mounted() {
    window.addEventListener('scroll', this.handleScroll);
    window.addEventListener('scroll', this.scrollToTop);
  },
  destroyed: function destroyed() {
    window.removeEventListener('scroll', this.handleScroll);
  }
};
